	(SELECT * FROM BOOK WHERE BTITLE LIKE '%'||#{searchText}||'%' OR BWRITER LIKE '%'||#{searchText}||'%' ORDER BY BTITLE) A)
	WHERE RN BETWEEN ${startRow} AND ${endRow}

